mehms\').timepicker({\r\n                minuteStep: 1,\r\n                showSeconds: true,\r\n                showMeridian: false,\r\n                defaultTime: false\r\n            });\r\n            $(\'#timepicker1\').timepicker({\r\n                minuteStep: 1,\r\n                showSeconds: true,\r\n                showMeridian: false,\r\n                defaultTime: false\r\n            });\r\n        </script>\r\n\r\n<script>\r\n$(document).ready(function() {\r\n    $(\'#datepicker0\')\r\n        .datepicker({\r\n            autoclose: true,\r\n            format: \'dd-mm-yyyy\',\r\n	    language: \'es\'\r\n        });\r\n    $(\'#datepicker1\')\r\n        .datepicker({\r\n            autoclose: true,\r\n            format: \'dd-mm-yyyy\',\r\n	    language: \'es\'\r\n        });\r\n    $(\'#datepicker2\')\r\n        .datepicker({\r\n            autoclose: true,\r\n            format: \'dd-mm-yyyy\',\r\n	    language: \'es\'\r\n        });\r\n    $(\'#datepicker3\')\r\n        .datepicker({\r\n            autoclose: true,\r\n            format: \'dd-mm-yyyy\',\r\n	    language: \'es\'\r\n        });\r\n    $(\'#datepicker4\')\r\n        .datepicker({\r\n            autoclose: true,\r\n            format: \'dd-mm-yyyy\',\r\n	    language: \'es\'\r\n        });\r\n    $(\'#datepicker5\')\r\n        .datepicker({\r\n            autoclose: true,\r\n            format: \'dd-mm-yyyy\',\r\n	    language: \'es\'\r\n        });\r\n});\r\n\r\n/**\r\n * Spanish translation for bootstrap-datepicker\r\n * Bruno Bonamin <bruno.bonamin@gmail.com>\r\n */\r\n;(function($){\r\n	$.fn.datepicker.dates[\'es\'] = {\r\n		days: [\"Domingo\", \"Lunes\", \"Martes\", \"Miércoles\", \"Jueves\", \"Viernes\", \"Sábado\"],\r\n		daysShort: [\"Dom\", \"Lun\", \"Mar\", \"Mié\", \"Jue\", \"Vie\", \"Sáb\"],\r\n		daysMin: [\"Do\", \"Lu\", \"Ma\", \"Mi\", \"Ju\", \"Vi\", \"Sa\"],\r\n		months: [\"Enero\", \"Febrero\", \"Marzo\", \"Abril\", \"Mayo\", \"Junio\", \"Julio\", \"Agosto\", \"Septiembre\", \"Octubre\", \"Noviembre\", \"Diciembre\"],\r\n		monthsShort: [\"Ene\", \"Feb\", \"Mar\", \"Abr\", \"May\", \"Jun\", \"Jul\", \"Ago\", \"Sep\", \"Oct\", \"Nov\", \"Dic\"],\r\n		today: \"Hoy\",\r\n		monthsTitle: \"Meses\",\r\n		clear: \"Borrar\",\r\n		weekStart: 1,\r\n		format: \"dd/mm/yyyy\"\r\n	};\r\n}(jQuery));\r\n</script>\r\n\r\n\r\n    </script>\r\n    <!-- /bootstrap-daterangepicker -->\r\n\r\n\r\n    <!-- Select2 -->\r\n    <script>\r\n      $(document).ready(function() {\r\n        $(\".select2_single\").select2({\r\n          placeholder: \"Seleccione.\",\r\n          allowClear: true\r\n        });\r\n        $(\".select2_group\").select2({});\r\n        $(\".select2_multiple\").select2({\r\n          maximumSelectionLength: 4,\r\n          placeholder: \"With Max Selection limit 4\",\r\n          allowClear: true\r\n        });\r\n      });\r\n    </script>\r\n    <!-- /Select2 -->\r\n\r\n    <!-- jQuery Tags Input -->\r\n    <script>\r\n      function onAddTag(tag) {\r\n        alert(\"Added a tag: \" + tag);\r\n      }\r\n\r\n      function onRemoveTag(tag) {\r\n        alert(\"Removed a tag: \" + tag);\r\n      }\r\n\r\n      function onChangeTag(input, tag) {\r\n        alert(\"Changed a tag: \" + tag);\r\n      }\r\n\r\n      $(document).ready(function() {\r\n        $(\'#tags_1\').tagsInput({\r\n          width: \'auto\'\r\n        });\r\n      });\r\n    </script>\r\n    <!-- /jQuery Tags Input -->\r\n\r\n\r\n    <!-- Autosize -->\r\n    <script>\r\n      $(document).ready(function() {\r\n        autosize($(\'.resizable_textarea\'));\r\n      });\r\n    </script>\r\n    <!-- /Autosize -->\r\n\r\n\r\n\r\n    <!-- Starrr -->\r\n    <script>\r\n      $(document).ready(function() {\r\n        $(\".stars\").starrr();\r\n\r\n        $(\'.stars-existing\').starrr({\r\n          rating: 4\r\n        });\r\n\r\n        $(\'.starrr\').on(\'click\', function (e) {\r\n          e.preventDefault();\r\n        });\r\n\r\n        $(\'.stars\').on(\'starrr:change\', function (e, value) {\r\n          $(\'.stars-count\').html(value);\r\n        });\r\n\r\n        $(\'.stars-existing\').on(\'starrr:change\', function (e, value) {\r\n          $(\'.stars-count-existing\').html(value);\r\n        });\r\n      });\r\n    </script>\r\n    <!-- /Starrr -->\r\n		<script type=\'text/javascript\'>\r\n			$(\'#editor\').wysiwyg(\r\n			{\r\n				\'form\':\r\n				{\r\n					\'text-field\': \'mySubmission\',\r\n					\'seperate-binary\': false\r\n				}\r\n			});\r\n		\r\n			$(\".dropdown-menu > input\").click(function (e) {\r\n        		e.stopPropagation();\r\n    		});\r\n		</script>\r\n\r\n<script>\r\nconsole.log(getCookieByName(\'USERNAME\'));\r\ndocument.getElementById(\"COOKIE\").value = getCookieByName(\'USERNAME\');\r\nvar coc = document.getElementById(\"COOKIE\").value;\r\n\r\nif (coc < \'*\') {\r\n   alert(\"No user logged in, please log in first.\");\r\n   var redir =\"/cgi-bin/COBLOGIN.EXE\";\r\n   window.location.href = redir;\r\n}\r\n\r\nfunction getCookieByName(name) {\r\n    const cookieString = document.cookie;\r\n    const cookies = cookieString.split(\';\');\r\n    for (let i = 0; i < cookies.length; i++) {\r\n        const cookie = cookies[i].trim();\r\n        if (cookie.startsWith(name + \'=\')) {\r\n            return cookie.substring(name.length + 1);\r\n        }\r\n    }\r\n    return null;\r\n}\r\n\r\n</script>\r\n\r\n<script language=\"Javascript\">\r\nfunction xmlhttpPost(strURL) {\r\n    var xmlHttpReq = false;\r\n    var self = this;\r\n    // Mozilla/Safari\r\n    if (window.XMLHttpRequest) {\r\n        self.xmlHttpReq = new XMLHttpRequest();\r\n    }\r\n    // IE\r\n    else if (window.ActiveXObject) {\r\n        self.xmlHttpReq = new ActiveXObject(\"Microsoft.XMLHTTP\");\r\n    }\r\n    self.xmlHttpReq.open(\'POST\', strURL, true);\r\n    self.xmlHttpReq.setRequestHeader(\'Content-Type\', \'application/x-www-form-urlencoded\');\r\n    self.xmlHttpReq.onreadystatechange = function() {\r\n        if (self.xmlHttpReq.readyState == 4) {\r\n            updatepage(self.xmlHttpReq.responseText);\r\n        }\r\n    }\r\n    self.xmlHttpReq.send(getquerystring());\r\n}\r\n\r\nfunction getquerystring() {\r\n    var qstr = \"session_UID\" + \"~\" + getCookieByName(\'session\') + \"~\" + getCookieByName(\'USERNAME\') + \"|\";\r\n    var form = document.forms[\'f1\'];\r\n    var x = document.getElementById(\"f1\").length;\r\n    for (var i = 0; i <x; i++) {\r\n       var name = form.elements[i].name;\r\n       var idx = form.elements[i].id;\r\n       var value = form.elements[i].value;\r\n       var type = form.elements[i].type;\r\n       var disa = idx.substr(0, 8);\r\n       if (type == \'checkbox\') {\r\n          var ckb = document.getElementById(name);\r\n             if ( ckb.checked == true ) {\r\n                 value = \'on\';\r\n             } else {\r\n                 value = \'off\';\r\n             }\r\n             // console.log (value);\r\n         }\r\n\r\n       if (type == \'radio\') {\r\n          if (document.getElementById(idx).checked == true ) {\r\n             value = \'on\';\r\n           } else {\r\n              value = \'off\';\r\n           }\r\n             //console.log (value);\r\n        }\r\n\r\n        if (type == \'textarea\') {\r\n           value = value.replace(/(?:\\r\\n|\\r|\\n)/g, \"<br>\");\r\n        }\r\n\r\n        if ( idx > \'*\' || name > \'*\' ) {\r\n           if (disa != \'disabled\') { \r\n               qstr = qstr + idx + \'~\' + type + \'~\' + value + \'|\';  // NOTE: no \'?\' before querystring\r\n           }\r\n        }\r\n    }\r\n    // console.log (qstr);\r\n    return qstr;\r\n}\r\n\r\nfunction updatepage(str){\r\n    document.getElementById(\"result\").innerHTML = str;\r\n}\r\n\r\n</script>\r\n'),
