(72, 'MENU', '<form name=\"f1\" id=\"f1\">       \r\n<input type=\"hidden\" id=\"PROGID\" name=\"PROGID\" value=\"MENU\">\r\n<input type=\"hidden\" id=\"COOKIE\" name=\"COOKIE\" value=\"\">    \r\n              <div class=\"row align-items-center\">\r\n              <div class=\"col-md-12 col-sm-12 col-xs-12\">\r\n                <div class=\"x_panel\">\r\n                  <div class=\"x_content text-center\">\r\n<h5>Manage Users: </h5>\r\n                   <input value=\"Users List\" id=\"list\" name=\"list\" type=\"button\" class=\"btn btn-info btn-round\" role=\"button\" onclick=\'javascript:clicked(\"list\")\' list>\r\n                 </div>\r\n              </div>\r\n            </div>\r\n            <div class=\"row\">\r\n              <div class=\"col-md-12 col-sm-12 col-xs-12\">\r\n                <div class=\"x_content text-center\">\r\n                  <input name=\"buttonck\" id=\"buttonck\" type=\"hidden\">\r\n                  <div id=\"result\"><h4>Message:</h4></div>\r\n                </div>\r\n              </div>\r\n            </div>\r\n            <div class=\"row\">\r\n              <div class=\"col-md-12 col-sm-12 col-xs-12\">\r\n                <div class=\"x_content text-center\">\r\n                   <input value=\"Exit, Logout\" id=\"exit\" name=\"exit\" type=\"button\" class=\"btn btn-primary btn-round\" role=\"button\" onclick=\'javascript:clicked(\"exit\")\' exit>\r\n                </div>\r\n              </div>\r\n            </div>\r\n</form>'),
(74, 'MENUJAVA', '<script>\r\nfunction clicked(btn) {\r\n  // alert (btn);\r\n  document.getElementById(\"buttonck\").value = btn;\r\n\r\n  if (btn == \'list\') {\r\n      var prog = \'COBUSRLST\';\r\n      var page = \'USRLST\';\r\n      var action =\'C\';\r\n      var active =\'N\';\r\n  }\r\n\r\n  if (btn == \'menu\') {\r\n      var prog = \'COBOLMENU\';\r\n      var page = \'MENU\';\r\n      var action =\'C\';\r\n      var active =\'N\';\r\n  }\r\n  if (btn == \'exit\') {\r\n      var prog = \'COBLOGIN\';\r\n      var page = \'LOGIN\';\r\n      var action =\'C\';\r\n      var active =\'N\';\r\n      alert (\'YOU HAVE LOGGEF OFF, PLEASE CLOSE DE BROWSER\');\r\n      document.cookie = \"USERNAME=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/cgi-bin;\";\r\n   }\r\n\r\n  var username = getCookieByName(\'USERNAME\');\r\n  username = username.padEnd(32, \'_\');\r\n  var cookie = getCookieByName(\'session\');\r\n  cookie = cookie.padEnd(32, \'_\');\r\n  prog = prog + \".EXE\";\r\n  var redir =\"/cgi-bin/\" + prog + \'?\' + action + page.padEnd(16, \'_\') + \'00000000000\' + active + prog.padEnd(16, \'_\') + username.replace(\' \',\'_\') + cookie.replace(\' \',\'_\');\r\n  var posts =\"/cgi-bin/\" + \'READPAGE.EXE\' + \'?\' + action + page.padEnd(16, \'_\') + \'00000000000\' + active + prog.padEnd(16, \'_\') + username.replace(\' \',\'_\') + cookie.replace(\' \',\'_\');\r\n  console.log (redir);\r\n  console.log (posts);\r\n  xmlhttpPost(posts);\r\n  window.location.href = redir;\r\n}\r\n\r\n</script>\r\n\r\n</html>\r\n'),
