(10, 'LOGIN', '<!DOCTYPE html>\r\n<html lang=\"es\">\r\n<head>\r\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\r\n    <link rel=\"icon\" href=\"../images/favicon.ico\" type=\"image/x-icon\" />\r\n    <!-- Bootstrap -->\r\n    <link href=\"../vendors/bootstrap/dist/css/bootstrap.min.css\" rel=\"stylesheet\">\r\n    <!-- Font Awesome -->\r\n    <link href=\"../vendors/font-awesome/css/font-awesome.min.css\" rel=\"stylesheet\">\r\n\r\n    <!-- Custom Theme Style -->\r\n    <link href=\"../css/login.css\" rel=\"stylesheet\">\r\n\r\n\r\n<form name=\"f1\" id=\"f1\" action=\"/cgi-bin/COBLOGIN.EXE\" method=\"post\">          \r\n<div class=\"wrapper\">\r\n        <div class=\"logo\">\r\n            <img src=\"/images/logo2.png\" alt=\"\">\r\n        </div>\r\n        <br>\r\n        <div class=\"text-center\">\r\n            <h5><b>GNUCobol HTML MariaDb</b></h5>\r\n        </div>\r\n         <div class=\"input-group\">\r\n            <div class=\"input-group\">\r\n                <span class=\"input-group-text\">\r\n                    <span class=\"fa fa-user fa-lg\" style=\"color:blue\"></span>\r\n                </span>                    \r\n            <input class=\"form-control\" placeholder=\"User Name or e@mail\" name=\"usuario\" id=\"usuario\">\r\n            </div>\r\n        </div>\r\n        <div class=\"input-group\" id=\"show_hide_password\">\r\n                <span class=\"input-group-text\">\r\n                    <span class=\"fa fa-lock fa-lg\" style=\"color:blue\"></span>\r\n                </span> \r\n               <input class=\"form-control\" placeholder=\"Password\" id=\"password\" name=\"password\" type=\"password\" value=\"\">\r\n                    <span class=\"input-group-text\"><a href=\"\"><i class=\"fa fa-eye\" aria-hidden=\"true\" style=\"color:blue\"></i></a></span>\r\n                    <span class=\"input-group-text\"><a href=\"\"><small></small></a></span>\r\n        </div>\r\n            <br>\r\n    